						],
						'inline' => [
							'name'      => __( 'Left', 'neve' ) . ' / ' . __( 'Right', 'neve' ),
							'url'       => 'data:image/png;base64,',
							'upsellUrl' => tsdk_utmify( 'https://themeisle.com/themes/neve/upgrade/', 'wooscardcontentalinginline' ),
						],
					],
